looksSummary.query:
query looksSummary($brand: String!, $division: String!, $season: String!) {
  looksSummary(brand: $brand, division: $division, season: $season) {
    hasDeliveryName
    hasDescription
    hasGender
    hasRelatedStyles
    hasTag
    hasTitle
    hasTrend
    totalLooks
  }
}

looks.query:
query looks($brand: String!, $season: String!, $division: String!) {
  looks(brand: $brand, season: $season, division: $division) {
    documentKey
    divisionCode
    lookType
    assetUrl
    title
    trend
    relatedStyles
    isDeleted
  }
}

getAllSeasonalAssignments.query:
query getAllSeasonalAssignments($styleSeasonCode: String!) {
  getAllSeasonalAssignments(styleSeasonCode: $styleSeasonCode) {
    brand
    brandName
    channels
    companyCode
    createdOn
    divisions {
      code
      isActive
      name
    }
    fms {
      season {
        code
        name
      }
      year
    }
    modifiedOn
    name
    salesOrganizationCodes
    styleSeasonCode
  }
}

getImageUrlCheck.query:
query getImageUrlCheck($divisions: [String!]!, $season: String!) {
  getImageUrlCheck(divisions: $divisions, season: $season) {
    divisionCode
    urls
  }
}

imageDetails.query:
query imageDetails($divisionCode: String!, $styleSeasonCode: String!) {
  imageDetails(divisionCode: $divisionCode, styleSeasonCode: $styleSeasonCode) {
    imageKey
    divisionCode
    styleSeasonCode
    imageUrl
    imageType
    isActive
    createdOn
    modifiedOn
  }
}
